let _upperLimit = 5000;
let _appInsightsTraces = materialize  (
    bctraces
    | where customDimensions.eventId == "RT0005"
    | where customDimensions.alObjectId > 0
);
let _lowerThresholdinMs = toscalar (
    _appInsightsTraces
    | limit 1
    | extend _executionTimeInMS = toreal(totimespan(customDimensions.executionTime))/10000
    | extend _longRunningThresholdInMS = iff(toreal(customDimensions.longRunningThresholdInMs) > 0, 
            toreal(customDimensions.longRunningThresholdInMs),
            toreal(totimespan(customDimensions.longRunningThreshold))/10000)
    | project tostring(toint(_longRunningThresholdInMS))
);
let _totalQueries = toscalar (
    _appInsightsTraces
    | count 
);
let _tooManyJoins = toscalar (
    _appInsightsTraces
    | extend sqlStatement = tostring(customDimensions.sqlStatement)
    | extend _numberOfJoins = countof(sqlStatement, "JOIN")
    | where _numberOfJoins > 5
    | count 
);
let _longQueries = toscalar (
    _appInsightsTraces
    | extend _executionTimeInMS = toreal(totimespan(customDimensions.executionTime))/10000
    | where _executionTimeInMS > toint(customDimensions.longRunningThresholdInMs) and _executionTimeInMS < _upperLimit
    | count 
);
let _veryLongQueries = toscalar (
    _appInsightsTraces
    | extend _executionTimeInMS = toreal(totimespan(customDimensions.executionTime))/10000
    | where _executionTimeInMS >= _upperLimit
    | count 
);
_appInsightsTraces
| limit 1
| project strcat(
                "|","Total Queries", 
                "|", "Long Running Queries", 
                "|", "Very Long Running Queries", 
                "|", "Queries with  many Joins", "|\n", 
                "|------------------|:---------------|:---------------|:---------------|\n",
                "|", _totalQueries, "|", _longQueries, "|", _veryLongQueries, "|", _tooManyJoins, "\n",
                "\n **Note:** \n",
                "- The **Lower Threshold** is defined as greater than ", _lowerThresholdinMs, " ms and less than ", _upperLimit, " ms\n",
                "- The **Upper Threshold** is defined greater than or equal to ", _upperLimit, " ms\n")

let _appInsightsTraces = materialize  (
    bctraces
    | where customDimensions.eventId == "RT0018"
    | extend _executionTimeInMS = toreal(totimespan(customDimensions.executionTime))/10000
    | extend _longRunningThresholdInMS = iff(toreal(customDimensions.longRunningThresholdInMs) > 0, 
            toreal(customDimensions.longRunningThresholdInMs),
            toreal(totimespan(customDimensions.longRunningThreshold))/10000)
    | where _executionTimeInMS > _longRunningThresholdInMS
    | project timestamp, message, ["alObjectType"] = toupper(customDimensions.alObjectType), customDimensions          
);
let _thresholdInMs = toscalar (
    _appInsightsTraces
    | limit 1
    | extend _executionTimeInMS = toreal(totimespan(customDimensions.executionTime))/10000
    | extend _longRunningThresholdInMS = iff(toreal(customDimensions.longRunningThresholdInMs) > 0, 
            toreal(customDimensions.longRunningThresholdInMs),
            toreal(totimespan(customDimensions.longRunningThreshold))/10000)
    | project tostring(toint(_longRunningThresholdInMS))
);
let _longPages = toscalar (
    _appInsightsTraces
    | where alObjectType == "PAGE" 
    | count 
);
let _longCodeunits = toscalar (
    _appInsightsTraces
    | where alObjectType == "CODEUNIT" 
    | count 
);
let _longReports = toscalar (
    _appInsightsTraces
    | where alObjectType == "REPORT" 
    | count 
);
let _longQueries = toscalar (
    _appInsightsTraces
    | where alObjectType == "QUERY" 
    | count 
);
let _longXMLPorts = toscalar (
    _appInsightsTraces
    | where alObjectType == "XMLPORT" 
    | count 
);
let _longTables = toscalar (
    _appInsightsTraces
    | where alObjectType == "TABLE" 
    | count 
);
let _longPageExtensions = toscalar (
    _appInsightsTraces
    | where alObjectType == "PAGEEXTENSION" 
    | count 
);
let _longTableExtensions = toscalar (
    _appInsightsTraces
    | where alObjectType == "TABLEEXTENSION" 
    | count 
);
let _longReportExtensions = toscalar (
    _appInsightsTraces
    | where alObjectType == "REPORTEXTENSION" 
    | count 
);
_appInsightsTraces
| limit 1
| extend Md = strcat('|', _longTables, 
                     '|', _longPages, 
                     '|', _longCodeunits, 
                     '|', _longReports, 
                     '|', _longXMLPorts,
                     '|', _longQueries,
                     '|', _longTableExtensions,
                     '|', _longPageExtensions,
                     '|', _longReportExtensions)
| summarize Md = make_list(Md)
| extend Md = strcat_array(Md, ' \n')
| extend Md = strcat('| Table | Page | Codeunit | Report | XMLport | Query | Table Extension | Page Extension | Report Extension |\n|'
                      '-------|-------|-------|-------|-------|-------|-------|-------|-------|\n ', Md)

let _appInsightsTraces = materialize  (
    bctraces
    | where customDimensions.eventId in ("AL0000E24", "AL0000E25", "AL0000E26")
);
let _startedEntries = toscalar (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000E25"
    | count
);
let _successEntries = toscalar (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000E26"
    | where customDimensions.alJobQueueResult == 'Success'
    | count
);
let _failedEntries = toscalar (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000E26"
    | where customDimensions.alJobQueueResult == 'Fail'
    | count
);
let _enqueuedEntries = toscalar (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000E24"
    | count
);
let _recurringEntries = toscalar (
     _appInsightsTraces
    | where customDimensions.eventId == "AL0000E24"
    | where tostring(customDimensions.alJobQueueIsRecurring) == 'Yes'
    | count 
);
_appInsightsTraces
| distinct tostring(customDimensions.alJobQueueId)
| limit 1
| extend Md = strcat('|', _startedEntries, 
                     '|', _successEntries, 
                     '|',  _failedEntries,
                     '|', (_startedEntries - _successEntries - _failedEntries), 
                     '|', (_enqueuedEntries - _recurringEntries), 
                     '|', _recurringEntries)
| summarize Md = make_list(Md)
| extend Md = strcat_array(Md, ' \n')
| extend Md = strcat('| Started | Successful | Failed | In Progress | Enqueued | Recurring|\n|'
                      '-------|-------|-------|-------|-------|-------|\n ', Md)

let _appInsightsTraces = materialize (
    bctraces
);
let _enqueuedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000E24"
    | summarize ["Enqueued Count"] = count() by bin(timestamp, 30min) 
);
let _startedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000E25"
    | summarize ["Started Count"] = count() by bin(timestamp, 30min)
);
let _finishedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000E26"
    | summarize ["Finished Count"] = count() by bin(timestamp, 30min)
);
let _failedToBeEnqueuedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000FNY"
    | summarize ["Failed to be enqueued Count"] = count() by bin(timestamp, 30min)
);
let _failedCanBeRetriedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000HE7"
    | summarize ["Failed Can Be Retried Count"] = count() by bin(timestamp, 30min)
);
let _RescheduledOnLoginJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000I49"
    | summarize ["Rescheduled On Login Count"] = count() by bin(timestamp, 30min)
);
let _RunOnceByUserJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000FMG"
    | summarize ["Run Once By User Count"] = count() by bin(timestamp, 30min)
);
let _FailedAndStoppedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000JRG"
    | summarize ["Failed And Stopped Count"] = count() by bin(timestamp, 30min)
);
_startedJQ
| join kind=leftouter ( _finishedJQ) on timestamp
| join kind=leftouter (_enqueuedJQ) on timestamp
| join kind=leftouter (_failedToBeEnqueuedJQ) on timestamp
| join kind=leftouter (_failedCanBeRetriedJQ) on timestamp
| join kind=leftouter (_RescheduledOnLoginJQ) on timestamp
| join kind=leftouter (_RunOnceByUserJQ) on timestamp
| join kind=leftouter (_FailedAndStoppedJQ) on timestamp
| project-away timestamp1, timestamp2, timestamp3, timestamp4, timestamp5, timestamp6, timestamp7
| sort by timestamp asc

let _limit = 4;
let _appInsightsTraces = materialize  (
    bctraces
    | where customDimensions.eventId == "RT0008"
    | project timestamp, message, customDimensions
);
let _soapThreshold = toscalar(
    _appInsightsTraces
    | where toupper(customDimensions.category) == "SOAP"
    | extend _processingTimeMS = toreal(totimespan(customDimensions.serverExecutionTime))/10000
    | summarize round(avg(_processingTimeMS),2)
);
let _soapCalls = toscalar( 
    _appInsightsTraces
    | where toupper(customDimensions.category) == "SOAP"
    | count
);
let _longRunningSoapCalls = toscalar(
    _appInsightsTraces
    | where toupper(customDimensions.category) == "SOAP"
    | extend _processingTimeMS = toreal(totimespan(customDimensions.serverExecutionTime))/10000
    | where _processingTimeMS > (_soapThreshold * _limit)
    | count
);
let _odataThreshold = toscalar(
    _appInsightsTraces
    | where toupper(customDimensions.category) contains "ODATA"
    | extend _processingTimeMS = toreal(totimespan(customDimensions.serverExecutionTime))/10000
    | summarize round(avg(_processingTimeMS),2)
);
let _odataCalls = toscalar(
    _appInsightsTraces
    | where toupper(customDimensions.category) contains "ODATA"
    | count
);
let _longRunningOdataCalls = toscalar(
    _appInsightsTraces
    | where toupper(customDimensions.category) == "ODATA"
    | extend _processingTimeMS = toreal(totimespan(customDimensions.serverExecutionTime))/10000
    | where _processingTimeMS > (_odataThreshold * _limit)
    | count
);
let _apiThreshold = toscalar(
    _appInsightsTraces
    | where toupper(customDimensions.category) == "API"
    | extend _processingTimeMS = toreal(totimespan(customDimensions.serverExecutionTime))/10000
    | summarize round(avg(_processingTimeMS),2)
);
let _apiCalls = toscalar(
    _appInsightsTraces
    | where toupper(customDimensions.category) == "API"
    | count
);
let _longRunningApiCalls = toscalar(
    _appInsightsTraces
    | where toupper(customDimensions.category) == "API"
    | extend _processingTimeMS = toreal(totimespan(customDimensions.serverExecutionTime))/10000
    | where _processingTimeMS > (_apiThreshold * _limit)
    | count
);
_appInsightsTraces
| limit 1
| project strcat("|","SOAP Calls", 
                 "|", "Average", 
                 "|", "Long Running", 
                 "|", " ", 
                 "|", "OData Calls", 
                 "|", "Average", 
                 "|", "Long Running", 
                 "|", " ", 
                 "|", "API Calls", 
                 "|", "Average", 
                 "|", "Long Running|\n",
                "|------------------|:---------------|:---------------|:---------------|:---------------|:---------------|:---------------|:---------------|:---------------|:---------------|:---------------|\n",
                "|", _soapCalls, 
                "|", _soapThreshold, 
                "|", _longRunningSoapCalls, 
                "|", "", 
                "|", _odataCalls, 
                "|", _odataThreshold, 
                "|", _longRunningOdataCalls, 
                "|", "",
                "|", _apiCalls, 
                "|", _apiThreshold, 
                "|", _longRunningApiCalls, "\n",
                "- **Long Running calls are those that are greater than 4x the Average**")

let utcOffsetHours = 2;
let _StartOfDay = datetime_add('hour',-utcOffsetHours,startofday(now()));
let _EndOfDay = datetime_add('hour',-utcOffsetHours,endofday(now()));
let data = bctraces
| where timestamp between (_StartOfDay .. _EndOfDay)
| where isnotempty(customDimensions.aadTenantId) // filter away signal from Docker sandboxes
| where customDimensions.alObjectId != '0' 
| where isnotempty(customDimensions.alObjectId)
| extend Title = tostring(message);
data
| where customDimensions.eventId in~ ('RT0028', 'RT0005', 'RT0012')
| summarize count() by Title

let _appInsightsTraces = materialize (
    bctraces
);
let _failedToBeEnqueuedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000FNY"
    | extend errorType = "Not Enqueued"
);
let _failedCanBeRetriedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000HE7"
    | extend errorType = "Can be Retried"
);
let _RescheduledOnLoginJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000I49"
    | extend errorType = "Rescheduled On Login"
);
let _RunOnceByUserJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000FMG"
    | extend errorType = "Run Once By User"
);
let _FailedAndStoppedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000JRG"
    | extend errorType = "Stopped"
);
_failedToBeEnqueuedJQ
| union (_failedToBeEnqueuedJQ) 
| union (_failedCanBeRetriedJQ) 
| union (_RescheduledOnLoginJQ)
| union (_RunOnceByUserJQ) 
| union (_FailedAndStoppedJQ) 
| project timestamp
    , errorType
    , description = customDimensions.alJobQueueObjectDescription
    , objectId = customDimensions.alJobQueueObjectId
    , objectName = customDimensions.alJobQueueObjectName
    , objectType = customDimensions.alJobQueueObjectType
    , earliestStartDateTime = customDimensions.alJobQueueEarliestStartDateTime
    , status = customDimensions.alJobQueueStatus
    , id = customDimensions.alJobQueueId
    , taskId = customDimensions.alJobQueueScheduledTaskId
| sort by timestamp asc

let _appInsightsTraces = materialize (
    bctraces
);
let _failedToBeEnqueuedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000FNY"
    | extend errorType = "Not Enqueued"
);
let _failedCanBeRetriedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000HE7"
    | extend errorType = "Can be Retried"
);
let _RescheduledOnLoginJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000I49"
    | extend errorType = "Rescheduled On Login"
);
let _RunOnceByUserJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000FMG"
    | extend errorType = "Run Once By User"
);
let _FailedAndStoppedJQ = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "AL0000JRG"
    | extend errorType = "Stopped"
);
_failedToBeEnqueuedJQ
| union (_failedToBeEnqueuedJQ) 
| union (_failedCanBeRetriedJQ) 
| union (_RescheduledOnLoginJQ)
| union (_RunOnceByUserJQ) 
| union (_FailedAndStoppedJQ) 
| extend hourofday(timestamp)
| summarize count() by errorType
    , description = tostring( customDimensions.alJobQueueObjectDescription)
    , objectId = toint(customDimensions.alJobQueueObjectId)
    , objectName = tostring(customDimensions.alJobQueueObjectName)
    , objectType = tostring(customDimensions.alJobQueueObjectType)
    , bin(timestamp, 1h)
| sort by timestamp asc

let _appInsightsTraces = materialize (
    bctraces
);
let _canceledTask = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "LC0044"
    | extend errorType = "Canceled"
);
let _failedTask = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "LC0045"
    | extend errorType = "Failed"
);
let _timeoutChangedTask = materialize (
    _appInsightsTraces
    | where customDimensions.eventId == "LC0057"
    | extend errorType = "Timeout Changed"
);
_canceledTask
| union _failedTask
| union _timeoutChangedTask
| project timestamp
    , errorType
    , result = customDimensions.result
    , message
    , objectId = customDimensions.codeunitObjectId
    , failedObjectId = customDimensions.codeunitFailedObjectId
    , isReady = customDimensions.isReady
    , notBefore = customDimensions.notBefore
    , attemptNumber = customDimensions.attemptNumber
    , cancelReason = tostring(customDimensions.cancelReason)
    , cancelReasonMessage = tostring(customDimensions.cancelReasonMessage)
    , serverExecutionTime = customDimensions.serverExecutionTime
    , totalTime = customDimensions.totalTime
    , sqlRowsRead = toint(customDimensions.sqlRowsRead)
    , sqlExecutes = toint(customDimensions.sqlExecutes)
    , sessionId = tostring(customDimensions.sessionId)
    , timeout = customDimensions.timeout
    , taskId = customDimensions.taskId
| sort by timestamp asc

let _interval = 1d;
let LRQ = (bctraces
| where 1==1 
    and timestamp >= todatetime(_startTime)
    and timestamp <= todatetime(_endTime) + totimespan(24h) - totimespan(1ms) 
    and customDimensions.eventId == 'RT0005'
);
LRQ
| make-series Count = count() on timestamp from _startTime to _endTime step _interval 
| extend (baseline, seasonal, trend, residual) = series_decompose(Count, -1, 'linefit')  //  decomposition of a set of time series to seasonal, trend, residual, and baseline (seasonal+trend)

let _interval = 1d;
let INCOMING = (traces
| where timestamp >= todatetime(_startTime)
    and timestamp <= todatetime(_endTime) + totimespan(24h) - totimespan(1ms) 
| where operation_Name == "Web Services Call" // do note that in a later version of the schema, this field will not be used 
     or customDimensions.eventId == "RT0008" // starting from version 16.1, the eventId is used to identity signal types
);     
INCOMING
| make-series Count = count() on timestamp from _startTime to _endTime step _interval 
| extend (baseline, seasonal, trend, residual) = series_decompose(Count, -1, 'linefit')  //  decomposition of a set of time series to seasonal, trend, residual, and baseline (seasonal+trend)
